mapper.insert:
INSERT INTO talks
(uuid, picture, picture, texts, create_time, update_time) VALUES
    (#{uuid}, #{picture}, #{picture}, #{texts}, #{createTime}, #{updateTime});

mapper.update:
UPDATE talks
SET
    picture = #{picture}, texts = #{texts}, update_time = #{updateTime}
WHERE uuid = #{uuid} AND user_uuid = #{userUuid};

mapper.select:
SELECT *
FROM talks
WHERE update_time  <  #{updateTime}
ORDER BY update_time DESC
LIMIT #{rows};
