mapper.select:
SELECT user_uuid,user_name
FROM user
WHERE user_name = #{userName};

SELECT user_uuid,user_name,examine_state
FROM user
WHERE user_uuid = #{userUuid};

SELECT user_name,user_uuid,password,head_img
FROM user
WHERE user_name = #{userName} AND password = #{password};

SELECT user_name,user_uuid
FROM user
WHERE user_uuid = #{userUuid} AND password = #{password};

mapper.update:
UPDATE user
SET head_img = #{headImg}
WHERE user_uuid = #{userUuid};
