mapper.update:
UPDATE befounder
SET age = #{age}, age_range = #{ageRange}, gender = #{gender}, state = #{state}
WHERE uuid = #{uuid};

UPDATE befounder
SET age = #{age}, age_range = #{ageRange}, gender = #{gender}, remarks = #{remarks}, state = #{state}
WHERE uuid = #{uuid};

mapper.select:
SELECT *
FROM befounder
ORDER BY update_time DESC LIMIT #{rows};

SELECT *
FROM befounder
WHERE update_time  <  #{updateTime}
ORDER BY update_time DESC LIMIT #{rows};

SELECT *
FROM befounder
WHERE update_time  >  #{updateTime}
ORDER BY update_time DESC LIMIT #{rows};
